import React from 'react';
import './Modal.css';
import Aux from '../../../Auxilury/Auxi';

const modal = (props) => (
  <Aux>
    <Backdrop/>
    <div className="Modal" style={{transform: props.show ? 'translateY(0)' : 'translateY(-100vh)', opacity: props.show ? '1' : '0'}}>
      {props.children}
    </div>
  </Aux>
);

export default modal;
