import React,  { Component } from 'react';
import { connect } from 'react-redux';
import Aux from '../../Auxilury/Auxi';
import Burger from '../../components/Burger/Burger';
import BuildControls from '../../components/Burger/BuildControls/BuildControls';
import Modal from '../../components/UI/Modal/Modal';
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary';
import AxiosInstance from '../../axios-orders';
import WithErrorHandler from '../../Auxilury/withErrorHandling/withErrorHandling';
import Spinner from '../../components/UI/Spinner/Spinner';
import * as actionTypes from '../../store/actions';

const INGREDIENT_PRICES = {
	salad: 1, 
	meat: 2, 
	bacon: 0.5,
	cheese: 3
};

class BurgerBuilder extends Component {
	constructor() {
		super();
		this.purchaseHandler = this.purchaseHandler.bind(this)
		this.modalCloseHandler = this.modalCloseHandler.bind(this)
		this.state = {
			ingredients : {
				meat: 0,
				cheese: 0,
				bacon: 0,
				salad: 0
			},
			purchasable: false,
			totalPrice: 4,
			purchasing: false,
			loading: false
		}
	}

	componentDidMount() {
		console.log(this.props)
		// AxiosInstance.get('orders.json')
		// .then(res => {
		// 	console.log(res)
		// })
		// .catch(err => {
		// 	console.log(err)
		// })
	}

	updatePurchaseState(ingredients) {
		const sum = Object.keys(ingredients).map(igKey => {
			return ingredients[igKey]
		}).reduce((sum, el) => {
			return sum + el;
		}, 0);
		this.setState({purchasable: sum > 0});
	}

	addIngredientsHandler = (type) => {
		const oldCount = this.props.ings[type];
		const updatedCount = oldCount + 1;
		const updatedIngredients = {...this.props.ings};
		updatedIngredients[type] = updatedCount;
		const priceAddition = INGREDIENT_PRICES[type];
		const oldPrice = this.state.totalPrice;
		const newPrice = oldPrice + priceAddition;
		this.setState({totalPrice: newPrice, ingredients: updatedIngredients});
		this.updatePurchaseState(updatedIngredients);
	}

	removeIngredientsHandler = (type) => {
		const oldCount = this.props.ings[type];
		if(oldCount <= 0) return;
		const updatedCount = oldCount > 1 ? oldCount - 1 : 0
		const updatedIngredients = {...this.props.ings};
		updatedIngredients[type] = updatedCount;
		const priceMinus = INGREDIENT_PRICES[type];
		const oldPrice = this.state.totalPrice;
		const newPrice = oldPrice - priceMinus;
		this.setState({totalPrice: newPrice, ingredients: updatedIngredients});
		this.updatePurchaseState(updatedIngredients);
	}

	purchaseHandler() {
		this.setState({purchasing: true})
	}

	modalCloseHandler() {
		this.setState({purchasing: false})
	}

	purchaseContinueHandler = () => {
		
		const queryParams = [];
		for(let i in this.props.ings) {
			queryParams.push(encodeURIComponent(i) + '=' + encodeURIComponent(this.props.ings[i]))
		}
		queryParams.push("price=" + this.state.totalPrice)
		const queryString = queryParams.join('&')
		this.props.history.push({
			pathname: '/checkout', 
			search: '?' + queryString
		});
	}


	render () {
		const disabledInfo = {...this.props.ings};
		let orderSummary = <OrderSummary
			price={this.state.totalPrice}
			purchaseContinue={this.purchaseContinueHandler}
			purchaseCancel={this.modalCloseHandler}
			ingredients={this.state.ingredients}
		/>
		if(this.state.loading) {
			orderSummary = <Spinner/>
		}

		for(let key in disabledInfo) {
			disabledInfo[key] = disabledInfo[key] <= 0 // {salad: true, meat: false ...}
		}
		return (
			<Aux>
				<Modal show={this.state.purchasing} modalClosed={this.modalCloseHandler}>
					{orderSummary}
				</Modal>
				<Burger ingredients = {this.state.ing}/>
					<BuildControls ordered={this.purchaseHandler} purchasable={this.state.purchasable} price={this.state.totalPrice} ingredientsAdded = {this.props.onIngredientAdd} ingredientRemoved = {this.props.onIngredientRemove} disabledInfo={disabledInfo}/>
			</Aux>
		)
	}
}
const mapStateToProps = state => {
	return {
		ings: state.ingredients
	};
}

const mapDispatchToProps = dispatch => { 
	return {
		onIngredientAdd: (ingredient) => dispatch({
			type: actionTypes.ADD_INGREDIENT,
			ingredientName: ingredient
		}),
		onIngredientRemove: (ingredient) => dispatch({
			type: actionTypes.REMOVE_INGREDIENT,
			ingredientName: ingredient
		})
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(WithErrorHandler(BurgerBuilder, AxiosInstance));
