import * as actionTypes from './actions';

const initialState = {
  ingredients: {
    salad: 0,
    meat: 0,
    bacon: 0,
    cheese: 0
  },
  totalPrice: 4
}

const reducer = (state = initialState, action) => {
  console.log(state)
  switch(action.type) {
    case actionTypes.ADD_INGREDIENT:
      return {
        ...state.initialState,
        ingredients: {
          ...state.initialState.ingredients,
          [action.ingredientName]: state.initialState.ingredients[action.ingredientName] + 1
        }
      }
    case actionTypes.REMOVE_INGREDIENT:
        return {
          ...state,
          ingredients: {
            ...state.initialState.ingredients,
            [action.ingredientName]: state.initialState.ingredients[action.ingredientName] - 1
          }
        }
    default:
      return {initialState}
  }
};

export default reducer;
