import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import Spinner from '../../../components/UI/Spinner/Spinner';
import './ContactData.css';
import AxiosInstance from '../../../axios-orders';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {
    state = {
        orderForm: {
            name: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'your name'
                },
                value: ''
            },
            address: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'your Address'
                },
                value: ''
            },
            phone: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'your Phone'
                },
                value: ''
            },
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'your email'
                },
                value: ''
            },
            deliveryMethod: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        {value: 'fastest', displayValue: 'Fastest'},
                        {value: 'cheapest', displayValue: 'Cheapest'}
                    ]
                },
                value: ''
            }
        },
        loading: false
    }

    orderHandler = (event) => {
        event.preventDefault(); // prevent to reload the page
        this.setState({loading: true})
		const order = {
			ingredients: this.props.ingredients,
			price: this.props.price,
			customer: {
				name: 'Ojas',
				address: '986 B Rani Bagh',
				phone: '99100****0',
				email: 'ojas.vwadhwa1995gmail.com',
			},
			deliveryMethod: 'Fastest'
		}

		// AxiosInstance.get('orders.json')
		// .then(res => {
		// 	console.log(res)
		// })
		// .catch(err => {
		// 	console.log(err)
		// })
		AxiosInstance.post('orders.json', order)
		.then(res => {
			console.log(res)
            this.setState({loading: false})
            this.props.history.push("/");
		})
		.catch(err => {
			console.log(err)
			this.setState({loading: false})
		})
    }

    inputChangeHandler = (event, inputIdentifier) => {
        console.log("TCL: ContactData -> inputChangeHandler -> inputIdentifier", inputIdentifier)
        console.log(event.target.value)
        const updatedOrderForm = {

        }
    }

    render () {
        const formElementArray = [];
        for(let key in this.state.orderForm) {
            formElementArray.push({
                id: key,
                config: this.state.orderForm[key]
            })
        }
        let form = (
            <form>
                <Input elementType="input" className="Input" elementConfig="" value=""/>
                {formElementArray.map(formElement => (
                    <Input changed={(event) => this.inputChangeHandler(event, formElement.id)} key={formElement.id} elementType={formElement.config.elementType} elementConfig={formElement.config.elementConfig} value={formElement.config.value}/>
                ))}
                <Button className="Input" btnType="Success" clicked={this.orderHandler}>Order</Button>
            </form>
        );
        if(this.state.loading) {
            form = <Spinner />
        }
        return (
            <div className="ContactData">
                <h4>Enter your contact data</h4>
                {form}
            </div>
        )
    }
}

export default ContactData;
