import React,  { Component } from 'react';
import Aux from '../../hoc/Aux';
import Burger from '../../components/Burger/Burger';
import BuildControls from '../../components/Burger/BuildControls/BuildControls';

const INGREDIENT_PRICES = {
	salad: 1, 
	meat: 2, 
	bacon: 0.5,
	cheese: 3
};

class BurgerBuilder extends Component {
	state = {
		ingredients : {
			meat: 0,
			cheese: 0,
			bacon: 0,
			salad: 0
		},
		totalPrice: 4
	}

	addIngredientsHandler = (type) => {
		const oldCount = this.state.ingredients[type];
		const updatedCount = oldCount + 1;
		const updatedIngredients = {...this.state.ingredients};
		updatedIngredients[type] = updatedCount;
		const priceAddition = INGREDIENT_PRICES[type];
		const oldPrice = this.state.totalPrice;
		const newPrice = oldPrice + priceAddition;
		this.setState({totalPrice: newPrice, ingredients: updatedIngredients});
	}

	removeIngredientsHandler = (type) => {
		const oldCount = this.state.ingredients[type];
		if(oldCount <= 0) return;
		const updatedCount = oldCount > 1 ? oldCount - 1 : 0
		const updatedIngredients = {...this.state.ingredients};
		updatedIngredients[type] = updatedCount;
		const priceMinus = INGREDIENT_PRICES[type];
		const oldPrice = this.state.totalPrice;
		const newPrice = oldPrice - priceMinus;
		this.setState({totalPrice: newPrice, ingredients: updatedIngredients})
	}

	render () {
		const disabledInfo = {...this.state.ingredients};
		for(key in disabledInfo) {
			disabledInfo 
		}
		return (
			<Aux>
				<Burger ingredients = {this.state.ingredients}/>
					<BuildControls ingredientsAdded = {this.addIngredientsHandler} ingredientRemoved = {this.removeIngredientsHandler}/>
			</Aux>
		)
	}
}
export default BurgerBuilder;
